#!/bin/bash
echo -e "\e[93mCreating directory \e[97m'wz2100 TURRET Systems separated'\e[93m in your home directory."
mkdir "$HOME/wz2100 TURRET Systems separated"
echo -e "\e[93mExtracting images using ImageMagick to the folder previously created."
convert "s1 Base.png" -crop 60x46+3+52\!   -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s1 L-A T VTOL CB Radar Turret.tif"
convert "s1 Base.png" -crop 60x46+3+100\!  -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s1 L-B T Nexus Link.tif"
convert "s1 Base.png" -crop 60x46+3+148\!  -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s1 L-C T Sensor Turret.tif"
convert "s1 Base.png" -crop 60x46+3+196\!  -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s1 L-D T Radar Detector.tif"
convert "s1 Base.png" -crop 60x46+65+52\!  -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s1 R-A T VTOL Strike Turret.tif"
convert "s1 Base.png" -crop 60x46+65+100\! -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s1 R-B T CB Radar Turret.tif"
convert "s1 Base.png" -crop 60x46+65+148\! -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s1 R-C T Wide Spectrum Sensor.tif"
convert "s1 Base.png" -crop 60x46+65+196\! -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s1 R-D T Jammer Turret.tif"
convert "s2 Base.png" -crop 60x46+3+52\!   -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s2 L-A T Truck.tif"
convert "s2 Base.png" -crop 60x46+3+100\!  -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s2 L-B T Heavy Repair Turret.tif"
convert "s2 Base.png" -crop 60x46+65+52\!  -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s2 R-A T Repair Turret.tif"
convert "s2 Base.png" -crop 60x46+65+100\! -channel rgba -fill none -opaque "#080840" "$HOME/wz2100 TURRET Systems separated/s2 R-B T Command Turret.tif"
echo -e "\e[93mCoping script \e[97m'wz2100 rename TURRET Systems.sh'\e[93m to the folder previously created."
cp "wz2100 rename TURRET Systems.sh" "$HOME/wz2100 TURRET Systems separated"
echo -e "\e[92mBatch process completed!"
